mapper.select:
select B.FOOD_CD ,C.CODE_NAME ,C.IMG FROM (SELECT A.FOOD_CD, MAX(A.STEP01) AS STEP01, MAX(A.STEP02) AS STEP02, MAX(A.STEP03) AS STEP03, MAX(A.STEP04) AS STEP04, MAX(A.STEP05) AS STEP05, MAX(A.STEP06) AS STEP06, MAX(A.STEP07) AS STEP07, MAX(A.STEP08) AS STEP08 FROM (SELECT FOOD_CD, CASE WHEN FOOD_STEP_TYPE = 'CM002' THEN FOOD_TYPE ELSE '' END AS STEP01, CASE WHEN FOOD_STEP_TYPE = 'CM003' THEN FOOD_TYPE ELSE '' END AS STEP02, CASE WHEN FOOD_STEP_TYPE = 'CM004' THEN FOOD_TYPE ELSE '' END AS STEP03, CASE WHEN FOOD_STEP_TYPE = 'CM005' THEN FOOD_TYPE ELSE '' END AS STEP04, CASE WHEN FOOD_STEP_TYPE = 'CM006' THEN FOOD_TYPE ELSE '' END AS STEP05, CASE WHEN FOOD_STEP_TYPE = 'CM007' THEN FOOD_TYPE ELSE '' END AS STEP06, CASE WHEN FOOD_STEP_TYPE = 'CM008' THEN FOOD_TYPE ELSE '' END AS STEP07, CASE WHEN FOOD_STEP_TYPE = 'CM009' THEN FOOD_TYPE ELSE '' END AS STEP08 from tb_foodMap) A GROUP BY A.FOOD_CD) B , TB_FOODINFO C WHERE B.FOOD_CD = C.CODE_ID AND DEL_YN = 'Y'
AND B.STEP01 = #{CM002}
AND B.STEP02 = #{CM003}
AND B.STEP03 = #{CM004}
AND B.STEP04 = #{CM005}
AND B.STEP05 = #{CM006}
AND B.STEP06 = #{CM007}
AND B.STEP07 = #{CM008}
AND B.STEP08 = #{CM009}
AND C.CODE_NAME = #{foodName}
GROUP BY C.CODE_NAME
